mapper.select:
select 
from bas_workorder a

where a.id=#{id}

select 
from bas_workorder a

where a.del_flag=#{DEL_FLAG_NORMAL}

    and a.orderCode like concat('%',#{orderCode},'%')


    and a.state!=#{state}

order by a.update_date desc

mapper.update:
update bas_workorder set
    del_flag=#{DEL_FLAG_DELETE}
where id=#{id}
