mapper.select:
select 
from bas_bom_detail a 
where a.id=#{id}

select 
from bas_bom_detail a 
where a.del_flag=#{DEL_FLAG_NORMAL}


		and b.bomName like concat('%',#{bom.bomName},'%')


	 and a.bom_id=#{bom.id}


order by bom_id

mapper.update:
update bas_bom_detail set 
	del_flag=#{DEL_FLAG_DELETE}
where id=#{id}
