mapper.select:
select

from bas_electronsteelyard a

where a.id=#{id}

select

from bas_electronsteelyard a

where a.del_flag = #{DEL_FLAG_NORMAL}

	and a.type=#{type}


	and a.spec=#{spec}


	and a.qrCode like concat('%',#{qrCode},'%')

order by a.type,a.spec,a.update_date desc

mapper.update:
update bas_electronsteelyard set qrCode = #{qrCode}, type = #{type}, spec = #{spec},
cell_id = #{workCell.id}, station_id = #{workStationInfos.id},
supplier = #{supplier}, manufacturer = #{manufacturer}, factoryNumber = #{factoryNumber}, purpose = #{purpose}, buyDate = #{buyDate}, person = #{person}, organization = #{organization.id}, ip = #{ip}, comBaudrate = #{comBaudrate}, comDataseat = #{comDataseat}, comBegin = #{comBegin}, comEnd = #{comEnd}, checkMode = #{checkMode}, sysParam = #{sysParam}, eSName = #{eSName}, eSRange = #{eSRange}, eSWeight = #{eSWeight}, update_by = #{updateBy.id}, update_date = #{updateDate} where id=#{id}

update bas_electronsteelyard set
	del_flag=#{DEL_FLAG_DELETE}
where id=#{id}
